package guru.springframework.recipeproject.domain;

import javax.persistence.*;
import java.util.Set;

// Defines the POJO as Entity
@Entity
public class Recipe {
    /// Properties
    /// ID - Auto generated Identity for relation DB
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;
    private String description;
    private Integer prepTime;
    private Integer cookTime;
    private Integer servings;
    private String source;
    private String url;
    private String directions;
    ///Enumerations
    @Enumerated(value = EnumType.STRING)
    private Difficulty difficulty;

    // @OneToOne - Define one to One relationship with Notes Entity
    ///cascade = CascadeType.ALL - Defines Recipe as the owner of the relation and all the cascade options will
    // be applicable to the child entity

    @OneToOne(cascade = CascadeType.ALL)
    private Notes notes;
    // defines Blob binary type in the DB
    @Lob
    private Byte[] image;

    // @OneToMany - Define one to Many relationship with Ingredient Entity and mapped with Recipe
    ///cascade = CascadeType.ALL - Defines Recipe as the owner of the relation and all the cascade options will
    // be applicable to the child entity

    @OneToMany(cascade = CascadeType.ALL, mappedBy = "recipe" )
    private Set<Ingredient> ingredients;

    @ManyToMany
    @JoinTable(name = "recipe_category",
            joinColumns = @JoinColumn(name = "recipe_id"),
            inverseJoinColumns = @JoinColumn(name = "category_id"))
    private Set<Category> categories;

    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public Integer getPrepTime() {
        return prepTime;
    }

    public void setPrepTime(Integer prepTime) {
        this.prepTime = prepTime;
    }

    public Integer getCookTime() {
        return cookTime;
    }

    public void setCookTime(Integer cookTime) {
        this.cookTime = cookTime;
    }

    public Integer getServings() {
        return servings;
    }

    public void setServings(Integer servings) {
        this.servings = servings;
    }

    public String getSource() {
        return source;
    }

    public void setSource(String source) {
        this.source = source;
    }

    public String getUrl() {
        return url;
    }

    public void setUrl(String url) {
        this.url = url;
    }

    public String getDirections() {
        return directions;
    }

    public void setDirections(String directions) {
        this.directions = directions;
    }

    public Notes getNotes() {
        return notes;
    }

    public void setNotes(Notes notes) {
        this.notes = notes;
    }

    public Byte[] getImage() {
        return image;
    }

    public void setImage(Byte[] image) {
        this.image = image;
    }

    public Set<Ingredient> getIngredients() {
        return ingredients;
    }

    public void setIngredients(Set<Ingredient> ingredients) {
        this.ingredients = ingredients;
    }

    public Difficulty getDifficulty() {
        return difficulty;
    }

    public void setDifficulty(Difficulty difficulty) {
        this.difficulty = difficulty;
    }

    public Set<Category> getCategories() {
        return categories;
    }

    public void setCategories(Set<Category> categories) {
        this.categories = categories;
    }
}
